<style>
    .inp-cbx {
        width: 16px;
        height: 16px;
        accent-color: #2563eb;
        /* ปรับสี checkbox ตามธีม Tailwind */
    }

    .checkbox-wrapper-46 {
        display: flex;
        align-items: center;
        gap: 0.5rem;
        padding: 0.25rem 0.5rem;
        border-radius: 4px;
        transition: background-color 0.2s ease, color 0.2s ease;
    }

    .checkbox-wrapper-46:hover {
        background-color: #ffeeee;
    }

    .checkbox-wrapper-46 label:hover {
        color: #c52929;
        cursor: pointer;
    }



    /* Css สำหรับ checkbox */
    .checkbox-wrapper-46 input[type="checkbox"] {
        display: none;
        visibility: hidden;
    }

    .checkbox-wrapper-46 .cbx {
        margin: auto;
        -webkit-user-select: none;
        user-select: none;
        cursor: pointer;
    }

    .checkbox-wrapper-46 .cbx span {
        display: inline-block;
        vertical-align: middle;
        transform: translate3d(0, 0, 0);
    }

    .checkbox-wrapper-46 .cbx span:first-child {
        position: relative;
        width: 18px;
        height: 18px;
        border-radius: 3px;
        transform: scale(1);
        vertical-align: middle;
        border: 1px solid #9098a9;
        transition: all 0.2s ease;
    }

    .checkbox-wrapper-46 .cbx span:first-child svg {
        position: absolute;
        top: 3px;
        left: 2px;
        fill: none;
        stroke: #ffffff;
        stroke-width: 2;
        stroke-linecap: round;
        stroke-linejoin: round;
        stroke-dasharray: 16px;
        stroke-dashoffset: 16px;
        transition: all 0.3s ease;
        transition-delay: 0.1s;
        transform: translate3d(0, 0, 0);
    }

    .checkbox-wrapper-46 .cbx span:first-child:before {
        content: "";
        width: 100%;
        height: 100%;
        background: #506eec;
        display: block;
        transform: scale(0);
        opacity: 1;
        border-radius: 50%;
    }

    .checkbox-wrapper-46 .cbx span:last-child {
        padding-left: 8px;
    }

    .checkbox-wrapper-46 .cbx:hover span:first-child {
        border-color: #506eec;
    }

    .checkbox-wrapper-46 .inp-cbx:checked+.cbx span:first-child {
        background: #506eec;
        border-color: #506eec;
        animation: wave-46 0.4s ease;
    }

    .checkbox-wrapper-46 .inp-cbx:checked+.cbx span:first-child svg {
        stroke-dashoffset: 0;
    }

    .checkbox-wrapper-46 .inp-cbx:checked+.cbx span:first-child:before {
        transform: scale(3.5);
        opacity: 0;
        transition: all 0.6s ease;
    }

    @keyframes wave-46 {
        50% {
            transform: scale(0.9);
        }
    }

    .dropdown-trigger-site .left-icon-site {
        transform: rotate(45deg);
    }

    .dropdown-trigger-site .right-icon-site {
        transform: rotate(-45deg);
    }

    .dropdown-trigger-site.active .left-icon-site {
        transform: rotate(135deg);
    }

    .dropdown-trigger-site.active .right-icon-site {
        transform: rotate(-135deg);
